import React, { Component } from 'react';
import TopBar from './components/TopBar';
import Book from './components/Book';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bookText: '',
      books: [],
      mockData: []
    }
  }

  componentDidMount() {
    fetch('http://5bc3685bce72500013c2a657.mockapi.io/api/books')
      .then(res => res.json())
      .then(json => {
        this.setState({ mockData: json })
      })
  }

  handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      let booksArr = this.state.books;
      booksArr.push(this.state.bookText);
      this.setState((prevState, props) => {
        return { bookText : '' };
      })
    }
  }

  updateBookText(bookText) {
    this.setState({ bookText: bookText.target.value })
  }

  deleteBook(index) {
    let booksArr = this.state.books;
    booksArr.splice(index, 1);
    this.setState({ books: booksArr })
  }

  addBook() {
    if (this.state.bookText === '') { return }

    let booksArr = this.state.books;
    booksArr.push(this.state.bookText);
    this.setState({ bookText: '' });
    this.textInput.focus();
  }

  render() {

    let mockData = this.state.mockData;

    let books = this.state.books.map((val, key) => {
      return <Book key={key} text={val}
        deleteMethod={() => this.deleteBook(key)} />
    })

    return (
      <div className="container">
        <TopBar/>
        {books}

        <ul>
          {
            mockData.map(item => (
              <li key={item.id}>
                {item.title} | {item.author} | {item.releaseDate}
              </li>
            ))
          }
        </ul>


        <div className="btn" onClick={this.addBook.bind(this)}>+</div>

        <input type="text"
          ref={((input) => { this.textInput = input })}
          className="textInput"
          value={this.state.bookText}
          onChange={bookText => this.updateBookText(bookText)}
          onKeyPress={this.handleKeyPress.bind(this)} />

      </div>
    );
  }
}

export default App;
