import React, { Component } from 'react';
import TopBar from './components/TopBar';
import BookCard from './components/BookCard';
import Input from '@material-ui/core/Input';
import BookForm from './components/BookForm';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bookText: '',
      books: [],
    }
  }

  componentDidMount() {
    fetch('http://5bc3685bce72500013c2a657.mockapi.io/api/books')
      .then(res => res.json())
      .then(json => {
        this.setState({ books: json })
      })
  }

  deleteBook(index) {
    fetch('http://5bc3685bce72500013c2a657.mockapi.io/api/books/'+ index, {
      method: 'delete'
    }).then(res => res.json());


    let booksArr = this.state.books;
    booksArr.splice(index, 1);
    this.setState({ books: booksArr })
  }

  handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      let booksArr = this.state.books;
      booksArr.push(this.state.bookText);
      this.setState((prevState, props) => {
        return { bookText: '' };
      })
    }
  }

  updateBookText(bookText) {
    this.setState({ bookText: bookText.target.value })
  }

  addBook() {
    if (this.state.bookText === '') { return }

    let booksArr = this.state.books;
    booksArr.push(this.state.bookText);
    this.setState({ bookText: '' });
    this.textInput.focus();
  }

  render() {

    let books = this.state.books.map((val, key) => {
      return <BookCard 
        key={key} 
        id={val.id}
        title={val.title} 
        author={val.author} 
        releaseDate={val.releaseDate}
        delete={this.deleteBook(val.id)}
        />
    })

    return (
      <div className="container">
        <TopBar />

        <BookForm/>

        {books}

      </div>
    );
  }
}

export default App;
